import React, { useState, useEffect } from 'react';
import { PaymentsService } from './PaymentsService.js';
import './App.css';

function App() {
  const [payments, setPayments] = useState([])
  const [payment, setPayment] = useState(0)

  useEffect(() => {
    PaymentsService.getPayments().then(res => setPayments(res))
  }, [payments])


  function handleSubmit(e) {
    e.preventDefault()
    PaymentsService.makePayment(payment)
  }

  return (
    <React.Fragment>
      {payments.map(({ description, amount }, idx) => (
        <p key={idx}>{description}: ${amount}</p>
      ))}
      <hr />
      <form onSubmit={handleSubmit}>
        <input type="number" onChange={(e) => setPayment(e.target.value)} />
        <input type="submit" value="Add Payment" />
      </form>
    </React.Fragment>
  );
}


