/**
 * # What is a Service?
 *
 * Services are an instance of something that persists for the lifetime
 * of your application. Think of them as background processes which your
 * presentational layer consumes.
 *
 * Services are normally objects that hold their own state and expose
 * behaviors via an API to affect their own state.
 */

const TodosService = () => {
  const todos = ["Do a thing", "Do another thing"];

  const getTodos = () => todos;

  const addTodo = (todo) => {
    todos.push(todo);
  };

  return { todos, getTodos, addTodo };
};

const PaymentsService = {
  payments: [
    { description: "Payment", amount: 25.0 },
    { description: "Payment", amount: 65.0 },
  ],

  getPayments() {
    return Promise.resolve([...this.payments]);
  },

  makePayment(amount) {
    return new Promise((resolve) => {
      setTimeout(() => {
        this.payments.push({ description: "Payment", amount });
        resolve();
      }, 1000);
    });
  },
};

export { PaymentsService, TodosService };
