  //   PaymentsService.makePayment(payment)
  // }

  return (
    <React.Fragment>
      {payments.map(({ description, amount }) => (
        <p key={amount}>{description}: ${amount}</p>
      ))}
      <hr />
      {/* <form onSubmit={handleSubmit}>
        <input onChange={e => setPayment(e.target.value)} />
        <input type="submit" value="Add Payment" />
      </form> */}
      <input onChange={e => setPayment(e.target.value)} />
      <button onClick={e => PaymentsService.makePayment(payment)}>submit</button>
    </React.Fragment>
  );
}


