import React, { useState, useEffect } from "react";
import { PaymentsService, TodosService } from "./PaymentsService.js";
import "./App.css";

function App() {
  const todosService = TodosService();

  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState("");

  useEffect(() => {
    setTodos(todosService.getTodos());
  }, []);

  function updateTodos() {
    todosService.addTodo(newTodo);
    console.log(todos);
  }

  function handleSubmit(e) {
    e.preventDefault();
    updateTodos();
  }

  return (
    <React.Fragment>
      <ul>
        {todos.map((todo, idx) => (
          <li key={idx}>{todo}</li>
        ))}
      </ul>
      <form onSubmit={handleSubmit}>
        <input type="text" onChange={(e) => setNewTodo(e.target.value)} />
        <input type="submit" />
      </form>
    </React.Fragment>
  );
}

