function App() {
  const [payments, setPayments] = useState([])
  const [payment, setPayment] = useState([])

  useEffect(() => {
    PaymentsService.getPayments().then(res => setPayments(res))
  }, [payments]);

  return (
    <React.Fragment>
      {payments.map(({ amount, description }) => {
        return <div key={amount}>{description} {amount}</div>
      })}

      <input onChange={e => setPayment(e.target.value)} />
      <button onClick={e => PaymentsService.makePayment(payment)}>submit</button>
    </React.Fragment>

  );
}

export default App;
