import React, { useState, useEffect } from 'react';
import { PaymentsService } from './PaymentsService.js';
import './App.css';

function App() {
  const [payments, setPayments] = useState([])
  const [payment, setPayment] = useState("")

  useEffect(() => {
    PaymentsService.getPayments().then(res => setPayments(res))
  }, [])

  function handleChange(e) {
    setPayment(e.target.value)
  }

  function handleSubmit(e) {
    e.preventDefault()
    PaymentsService.makePayment(payment)
  }

  return (
    <React.Fragment>
      {payments.map(({description, amount}, idx) => (
        <p key={idx}>{description}: ${amount}</p>
      ))}
      <hr />
      <form onSubmit={handleSubmit}>
        <input type="text" onChange={handleChange} value={payment} />
        <input type="submit" value="Add Payment" />
      </form>
    </React.Fragment>
  );
}

